const {__} = wp.i18n;

jQuery(document).ready(function ($) {
	$('.phone').mask('+38(000)000-00-00')

	jcf.replace($('.pop-cont input:checkbox'))
	jcf.replace($('.pop-cont input:file'))

	$('.photo input:file').hover(
		function () {
			$('.jcf-file .jcf-upload-button').toggleClass('hov')
		})
	var maxFileSize = 4 * 1024 * 1024 // (байт) Максимальный размер файла (4мб)
	var queue = {}
	var form = $('form#uploadImages')
	var imagesList = $('#uploadImagesList')

	var itemPreviewTemplate = imagesList.find('.item.template').clone()
	itemPreviewTemplate.removeClass('template')
	imagesList.find('.item.template').remove()

	$('#addImages').on('change', function () {
		var files = this.files

		if (this.files.length > 10)
			alert(__('You can add up to 10 files', 'tehnokrat'))
		else {
			for (var i = 0; i < files.length; i++) {
				var file = files[i]

				if (!file.type.match(/image\/(jpeg|jpg|png|gif)/)) {
					alert(__('The photo must be in .jpg, .png or .gif format', 'tehnokrat'))
					continue
				}

				if (file.size > maxFileSize) {
					alert(__('Photo size must not exceed 4 MB', 'tehnokrat'))
					continue
				}

				preview(files[i])
			}

			this.value = ''
		}
	})

	// Создание превью
	function preview(file) {
		var reader = new FileReader()
		reader.addEventListener('load', function (event) {
			var img = document.createElement('img')

			var itemPreview = itemPreviewTemplate.clone()

			itemPreview.find('.img-wrap img').attr('src', event.target.result)
			itemPreview.data('id', file.name)

			imagesList.append(itemPreview)

			queue[file.name] = file

		})
		reader.readAsDataURL(file)
	}

	// Удаление фотографий
	imagesList.on('click', '.delete-link', function () {
		var item = $(this).closest('.item'),
			id = item.data('id')

		delete queue[id]

		item.remove()
	})

	// Отправка формы.
	form.on('submit', event => {
		event.preventDefault()

		if (Object.keys(queue).length > 10) {
			alert(__('You can add up to 10 files', 'tehnokrat'))
			return false
		}

		const form = $('form#uploadImages')

		form.block({
			message: null,
			overlayCSS: {
				background: '#fff',
				opacity: 0.6
			}
		})

		$('input[type=submit]', form).hide()

		grecaptcha.ready(() => grecaptcha.execute(tehnokrat.recaptchaSiteKey).then(token => {
			const formData = new FormData(document.forms.uploadImages)

			$('div.result', form).text('')

			for (let id in queue) {
				formData.append('images[]', queue[id])
			}

			formData.append('action', 'tehnokrat_submit_trade_in_pop_up')
			formData.append('token', token)

			$.ajax({
				url: tehnokrat.ajax_url,
				type: 'POST',
				data: formData,
				async: true,
				success: res => {
					if (true === res.success) {
						$('div.result', form)
							.toggleClass('success', true)
							.toggleClass('error', false)
							.text(__('Request sent successfully. The answer will be sent to you by e-mail within 2 hours (during business hours).', 'tehnokrat'))
					} else {
						console.log(res)
						$('div.result', form)
							.toggleClass('success', false)
							.toggleClass('error', true)
							.text(__('Failed to send request. Please reload the page and try again.', 'tehnokrat'))
					}
					$(form).unblock()
				},
				cache: false,
				contentType: false,
				processData: false
			})
		}))

		return false
	})
})
